import axios from 'axios'

const url="http://localhost:8080"

export const read=async(id)=>{
    const t = await axios.get(`${url}/{}`)
}

export const viewing=async()=>{
    const t = await axios.get(`${url}/`)
    return t;
}

export const adding=async(obj)=>{
    const t = await axios.post(`${url}/`,obj)
    return t;
}
